import { StatusBar } from "expo-status-bar";
import React from "react";
// import { StyleSheet } from 'react-native';
import {
  SafeAreaView,
  StyleSheet,
  Button,
  TextInput,
  Text,
  View,
} from "react-native";
export default function App() {
  const [email, setEmail] = React.useState("");
  const [password, setPassword] = React.useState("");

  const handleSubmit = () => {
    console.log(email, password);
  };
  return (
    <View style={styles.container}>
      <View style={styles.headerWrapper}>
        <Text>Login</Text>
      </View>
      <StatusBar style="auto" />
      <TextInput
        value={email}
        onChangeText={(text) => setEmail(text)}
        style={styles.input}
        placeholder="Email"
      />
      <TextInput
        style={styles.input}
        value={password}
        onChangeText={(text) => setPassword(text)}
        placeholder="password"
        secureTextEntry={true}
      />

      <Button
        style={styles.button}
        onPress={handleSubmit}
        title="Login"
        color="#841584"
        accessibilityLabel="Learn more about this purple button"
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },

  input: {
    width: 300,
    height: 50,
    borderColor: "#000000",
    borderWidth: 1,
    margin: 10,
    padding: 10,
    borderRadius: 10,
    backgroundColor: "#fff",
    fontSize: 20,
    fontWeight: "semibold",
    color: "#000",
    border: 0,
    borderBottomColor: "red",
    borderBottomWidth: 2,
    marginBottom: 30,
  },

  headerWrapper: {
    borderBottomColor: "red",
    borderBottomWidth: 2,
    marginBottom: 30,
  },

  button: {
    width: 300,
    height: 50,
    borderColor: "#000000",
    borderWidth: 1,
    margin: 10,
    padding: 50,
    borderRadius: 50,
    backgroundColor: "#fff",
    fontSize: 20,
    fontWeight: "bold",
  },
});
